import React, { useState } from 'react';

import Expenses from './components/Expenses/Expenses';
import ExpenseForm from './components/Expense/ExpenseForm';
import { EXPENSES } from './constants';

const App = () => {
  const [expenses, setExpenses] = useState(EXPENSES);
  const [currentExpense, setCurrentExpense] = useState(null);

  const addExpenseHandler = (newExpense) => {
    setExpenses(prevState => ([...prevState, newExpense]));
  };

  const removeExpenseHandler = (expenseId) => {
    const updatedExpenses = expenses.filter((expense) => expense.id !== expenseId);
    setExpenses([...updatedExpenses]);
  };

  const editExpenseHandler = (expenseId) => {
    const currentExpense = expenses.find((expense) => expense.id == expenseId);
    setCurrentExpense(currentExpense);
  }

  return (
    <div>
      <ExpenseForm onAddExpense={addExpenseHandler} expense={editExpense} />
      <Expenses
        items={expenses}
        onRemoveExpenseData={removeExpenseHandler}
        onEditExpenseData={editExpenseHandler}
      />
    </div>
  );
}

export default App;
