import React, { useState } from 'react';
import './NewExpenseForm.css';

const NewExpenseForm = (props) => {
  const initialUserInput = {
    enteredTitle: '',
    enteredAmount: '',
    enteredDate: '',
    url: ''
  };

  const [attachedImage, setAttachedImage] = useState('');
  const [userInput, setUserInput] = useState(initialUserInput);

  const changeHandler = (event) => {
    const { name, value } = event.target;
    if (name === 'attachedImage') {
      setAttachedImage(event.target.files[0]);  
    } else {
      setUserInput(prevState => ({ ...prevState, [name]: value }));
    }
  }

  const uploadImageHandler = async (event) => {
    const data = new FormData();
    data.append('file', attachedImage);
    data.append('upload_preset', 'j0j1j50n');
    data.append('cloud_name', 'dzjciizhx');
    const response = await fetch("https://api.cloudinary.com/v1_1/dzjciizhx/image/upload", {
      method: 'POST',
      body: data
    });
    const responseData = await response.json();
    console.log(responseData);
    console.log('=====================');
    setUserInput(prevState => ({ ...prevState, url: responseData.url }));
  }

  const submitHandler = (event) => {
    event.preventDefault();

    props.onSaveExpenseData({
      title: userInput.enteredTitle,
      amount: userInput.enteredAmount,
      date: new Date(userInput.enteredDate)
    });
    setUserInput(initialUserInput);
  }

  return (
    <form onSubmit={submitHandler}>
      <div className='new-expense__controls'>
        <div className='new-expense__control'>
          <label htmlFor='title'>Title</label>
          <input
            type='text'
            id='title'
            value={userInput.enteredTitle}
            name='enteredTitle'
            onChange={changeHandler}
          />
        </div>
        <div className='new-expense__control'>
          <label htmlFor='amount'>Amount</label>
          <input
            type='number'
            id='amount'
            value={userInput.enteredAmount}
            name='enteredAmount'
            min='0.01'
            step='0.01'
            onChange={changeHandler}
          />
        </div>
        <div className='new-expense__control'>
         <label htmlFor='date'>Date</label>
          <input
            type='date'
            id='date'
            min='2021-01-01'
            max='2022-12-31'
            value={userInput.enteredDate}
            name='enteredDate'
            onChange={changeHandler}
          />
        </div>
        <div className='new-expense__control'>
          <div className='new-expense__image'>
            <input type='file' onChange={changeHandler} name="attachedImage" />
            <button onClick={uploadImageHandler}>Upload</button>
          </div>
        </div>
      </div>
      <div className='new-expense__actions'>
        <button type='submit'>Add Expense</button>
      </div>
    </form>
  ) 
}

export default NewExpenseForm;
