import React, { useState } from 'react';
import { TITLE_REGEX, NUMBER_REGEX } from '../../constants'
import './NewExpenseForm.css';

const NewExpenseForm = (props) => {
  const initialUserInput = {
    enteredTitle: '',
    enteredAmount: '',
    enteredDate: '',
    imageUrl: ''
  };

  const [userInput, setUserInput] = useState(initialUserInput);
  const [userInputError, setuserInputError] = useState(initialUserInput);

  const changeHandler = (event) => {
    const { name, value } = event.target;
    setUserInput(prevState => ({ ...prevState, [name]: value }));
  }

  const uploadImageHandler = async (event) => {
    event.preventDefault();

    const data = new FormData();
    data.append('file', event.target.files[0]);
    data.append('upload_preset', 'j0j1j50n');
    data.append('cloud_name', 'dzjciizhx');
    const response = await fetch("https://api.cloudinary.com/v1_1/dzjciizhx/image/upload", {
      method: 'POST',
      body: data
    });
    const responseData = await response.json();
    setUserInput(prevState => ({ ...prevState, imageUrl: responseData.url }));
  }

  const validateInput = () => {
    const errors = {...initialUserInput};
    console.log(userInput.enteredAmount);
    console.log(Object.keys(userInput));
    console.log('============== Object');
    Object.keys(userInput).forEach((key) => {
      console.error('==============');
      console.error(key);
      console.log(value);
      console.log(NUMBER_REGEX.test(value));
      switch (key) {
        case 'enteredTitle':
          if (!TITLE_REGEX.test(value)) {
            errors[key] = 'Invalid Title, Please correct it.'
          }
          break;
        case 'enteredAmount':
          if (!NUMBER_REGEX.test(value) || value === 0) {
            console.error('============ test ===========')
            errors[key] = 'Invalid or empty Amount Entered.';
          }
          break;
      }
      return errors;
    })
  }

  const submitHandler = (event) => {
    event.preventDefault();
    const errors = validateInput();
    console.log(errors);
    props.onSaveExpenseData({
      title: userInput.enteredTitle,
      amount: userInput.enteredAmount,
      date: new Date(userInput.enteredDate),
      url: userInput.imageUrl
    });
    setUserInput(initialUserInput);
  }

  console.log(userInput);
  return (
    <form onSubmit={submitHandler}>
      <div className='new-expense__controls'>
        <div className='new-expense__control'>
          <label htmlFor='title'>Title</label>
          <input
            type='text'
            id='title'
            value={userInput.enteredTitle}
            name='enteredTitle'
            onChange={changeHandler}
          />
        </div>
        <div className='new-expense__control'>
          <label htmlFor='amount'>Amount</label>
          <input
            type='number'
            id='amount'
            value={userInput.enteredAmount}
            name='enteredAmount'
            min='0.01'
            step='0.01'
            onChange={changeHandler}
          />
        </div>
        <div className='new-expense__control'>
         <label htmlFor='date'>Date</label>
          <input
            type='date'
            id='date'
            min='2021-01-01'
            max='2022-12-31'
            value={userInput.enteredDate}
            name='enteredDate'
            onChange={changeHandler}
          />
        </div>
        <div className='new-expense__control'>
          <div className='new-expense__image'>
            <label className="new-expense__image-label">
              <input type="file" onChange={uploadImageHandler} />
              <i className="fa fa-cloud-upload" /> Attach Image
            </label>
          </div>
        </div>
      </div>
      <div className='new-expense__actions'>
        <button type='submit'>Add Expense</button>
      </div>
    </form>
  ) 
}

export default NewExpenseForm;
