import React, { useState } from 'react';
import Card from '../UI/Card';
import ExpenseFilter from './ExpenseFilter';
import ExpenseList from './ExpenseList';
import './Expenses.css';

const Expenses = (props) => {
  const [filterSelect, setFilterSelect] = useState('');
  const [filterExpenses, setFilterExpenses] = useState(props.items);

  const changeFilterHandler = (filterYear) => {
    filterItems = props.items.filter((item) => item.date.getFullYear() === Number(filterYear))
  }

  return (
    <Card className="expenses">
      <ExpenseFilter selected={filterSelect} onChangeFilter={changeFilterHandler}/>
      <ExpenseList
        items={filterExpenses}
        onRemoveExpenseData={props.onRemoveExpenseData}
      />
    </Card>
  )
}

export default Expenses;
