import React, { useState } from 'react';

import Expenses from './components/Expenses/Expenses';
import NewExpense from './components/NewExpense/NewExpense';
import { EXPENSE_CONSTANT } from './constants/ExpenseConstants';

const App = () => {
  const [expenses, setExpenses] = useState(EXPENSE_CONSTANT);

  const addExpenseHandler = (newExpense) => {
    setExpenses(prevState => ([...prevState, newExpense]));
  };

  const removeExpenseHandler = (expenseId) => {
    const expenseIndex = expenses.findIndex(expense => expense.id === expenseId);
    const filteredExpenses = expenses.splice(expenseIndex, 1);
    // const filteredExpenses = expenses.filter((expense) => expense.id != expenseId );
    setExpenses(filteredExpenses);
  };

  return (
    <div>
      <NewExpense onAddExpense={addExpenseHandler}/>
      <Expenses items={expenses} onRemoveExpenseData={removeExpenseHandler} />
    </div>
  );
}

export default App;
