import React, { useState } from 'react';
import Card from '../UI/Card';
import ExpenseFilter from './ExpenseFilter';
import ExpenseList from './ExpenseList';
import './Expenses.css';

const Expenses = (props) => {
  const [filterSelect, setFilterSelect] = useState('');
  const [filterExpenses, setFilterExpenses] = useState('');

  const changeFilterHandler = (filterYear) => {
    const filterItems = props.items.filter((item) => {
      return (item.date.getFullYear() === Number(filterYear))
    });
    setFilterSelect(filterYear);
    setFilterExpenses(filterItems);
  }

  const clearFilterHandler = () => {
    setFilterSelect('');
    setFilterExpenses(props.items);
  }

  console.log(filterExpenses);
  console.log('Filter Expense ============');
  return (
    <Card className="expenses">
      <ExpenseFilter
        selected={filterSelect}
        onChangeFilter={changeFilterHandler}
        onClearFilter={clearFilterHandler}
      />
      <ExpenseList
        items={filterExpenses}
        onRemoveExpenseData={props.onRemoveExpenseData}
      />
    </Card>
  )
}

export default Expenses;
